package dp;

public class Comparison_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int dp = 1000;
		int kp = 500;
		
		// if dp is equal to kp then it returns true
		System.out.println("dp == kp = " + (dp == kp) );
		
		// if dp is not equal to kp then it returns true
	    System.out.println("dp != kp = " + (dp != kp) );
	    
		// if dp is greater than kp then it returns true
	    System.out.println("dp > kp = " + (dp > kp) );
	    
		// if dp is less than kp then it returns true
	    System.out.println("dp < kp = " + (dp < kp) );
	    
		// if dp is greater than or equal to kp then it returns true
	    System.out.println("dp >= kp = " + (dp >= kp) );
	    
		// if num1 is less than or equal to num2 then it returns true
	    System.out.println("num1 <= num2 = " + (dp <= kp) );
	}

}
