package dp;

public class Logical_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean Value_1 = true;
		boolean Value_2 = true;
		
		// && - AND
		System.out.println("Output of AND is "+ (Value_1&&Value_2));
		// || - OR
		System.out.println("Output of OR is "+ (Value_1||Value_2));
		// ! - NOT
		System.out.println("Output of NOT is "+ (!Value_1));
	}

}
